				$sheetMillions .= '</tr>';
				
			}
			$sheetMillions .= '<tr><td colspan ="100%" style ="padding:4px;border:1px solid rgb(175, 171, 146);border-top:0;background-color:rgb(235, 235, 230);"></td></tr>';
			$sheetMillions .= '</table>';
			return $sheetMillions;	
		}
