		$row = 0;
		while($data = $this->fetch_array($record)){
			
			$trackingNumber = $data['TrackingNumber'];
			$supplier = utf8_encode($data['Claimant']);
			$amount = $data['PO_Amount'];
			if($data['TotalAmountMultiple'] > 0) {
